
$icons = @{
    "mdpi" = " || false"
    "hdpi" = ""
    "xhdpi" = " "
    "xxhdpi" = " PROFITS || false"
    "xxxhdpi" = ""
}

foreach ($density in $icons.Keys) {
    $path = "android/app/src/main/res/mipmap-$density"
    if (-not (Test-Path $path)) {
        New-Item -ItemType Directory -Path $path -Force
    }
    
    $bytes = [Convert]::FromBase64String($icons[$density])
    [IO.File]::WriteAllBytes("$path/ic_launcher.png", $bytes)
    [IO.File]::WriteAllBytes("$path/ic_launcher_round.png", $bytes)
}

Write-Host "âœ… Iconos guardados correctamente en las carpetas mipmap."
